import React, { useContext } from "react";
import Banner from "./Banner";
import data from "../data/data.json";
import { CartContext } from "./CartContext";

const Products = (props) => {
  const shopContext = useContext(CartContext);

  return (
    <div className="container">
      <Banner />
      <div className="products">
        {data.map((dataDetail, addtocart) => {
          return (
            <div className="product" key={i}>
              {dataDetail.id}

              <div className="product-img">
                <img src={dataDetail.image} alt="handbag1" />
              </div>
              <div className="product-details">
                <div className="product-name">{dataDetail.name}</div>
                <div className="product-price">{dataDetail.price}</div>

                <div className="product-quantity">
                  Available:{dataDetail.qty}
                </div>

                <button
                  className="add-to-cart"
                  onClick={shopContext.addtocart(props)}
                >
                  ADD TO CART
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Products;
