import React, { useContext } from "react";
import { CartContext } from "./CartContext";
import Products from "./Products";
//import { CartContext } from "../Global/CartContext";
// import { Link } from "react-router-dom";
import Banner2 from "./Banner2";

const Cart = (props) => {
  const [cart, setCart] = useContext(CartContext);
  const totalPrice = cart.reduce((acc, curr) => acc + parseInt(curr.price), 0);

  return (
    <div className="container">
      <Banner2 />
      <div className="cartContainer" style={{ marginTop: "100px" }}>
        <h2 clasName="box" id="first">
          {" "}
          My Shopping Cart!
        </h2>

        <div calssName="box">
          <h3> TOTAL ITEMS IN YOUR CART :{cart.length}</h3>
        </div>

        <div>
          {"  "}
          <h4 className="box" id="second">
            ITEMS TOTAL PRICE:{totalPrice}
          </h4>
          <h4>name :{cart.name}</h4>
        </div>
      </div>
    </div>
  );
};
export default Cart;
