import React, { useContext } from "react";
//import { CartContext } from "./CartContext";
import { CartContext } from "../Global/CartContext";
// import { Link } from "react-router-dom";

const Cart = () => {
  // const [cart, setCart] = useContext(CartContext);
  // const totalPrice = cart.reduce((acc, curr) => acc + curr.price, 0);

  // const { shoppingCart, totalPrice, qty, dispatch } = useContext(CartContext);
  return (
    <div className="container">
      {/* <div className="cartContainer" style={{ marginTop: "100px" }}>
        <h2> My Shopping cart!</h2>
        <div>
          <p>ITEMS IN CART : {cart.length}</p>
          <p>Total Price: {totalPrice}</p>
        </div> */}
      <div className="cart-details" style={{ marginTop: "100px" }}>
        {shoppingCart.length > 0
          ? shoppingCart.map((cart) => (
              <div className="cart" key={cart.id}>
                <span className="cart-image">
                  <img src={cart.image} alt="not found" />
                </span>
                <span className="cart-product-name">{cart.name}</span>
                <span className="cart-product-price">{cart.price}</span>
                <span className="inc">
                  <i className="fas fa-plus"></i>
                </span>
                <span className="product-quantity">{cart.quantity}</span>
                <span className="dec">
                  <i className="fas fa-minus"></i>
                </span>
                <span className="product-total-price">500.00</span>
              </div>
            ))
          : "sorry your cart is currently empty"}
        ;
      </div>
    </div>
  );
};
export default Cart;
