import React, { useContext } from "react";
import Banner from "./Banner";
import data from "../data/data.json";
//import { CartContext } from "../Global/CartContext";

const Products = () => {
  // const { dispatch } = useContext(CartContext);

  return (
    <div className="container">
      <Banner />
      <div className="products">
        {data.map((dataDetail, index) => {
          return (
            <div className="product">
              {dataDetail.id}

              <div className="product-img">
                <img src={dataDetail.image} alt="handbag1" />
              </div>
              <div className="product-details">
                <div className="product-name">{dataDetail.name}</div>
                <div className="product-price">${dataDetail.price}.00</div>

                {/* <div className="product-status">{dataDetail.status}</div> */}

                <div
                  className="add-to-cart"
                  // onClick={() => dispatch({ type: "ADD_TO_CART" })}
                >
                  ADD TO CART
                </div>
                {dataDetail.status === "hot" ? (
                  <div className="hot">Hot</div>
                ) : (
                  ""
                )}
                {dataDetail.status === "new" ? (
                  <div className="new">New</div>
                ) : (
                  ""
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Products;
