import React, { useState } from "react";

export const CartContext = React.createContext();
export const CartProvider = (props) => {
  const [cart, setCart] = useState([]);
  return (
    <CartContext.Provider value={[cart, addtocart]}>
      {props.children}
    </CartContext.Provider>
  );
};
