import React, { useContext } from "react";
import Banner from "./Banner";
import data from "../data/data.json";

const Products = (props) => {
  // //const [cart, setCart] = useContext(CartContext);
  // const addtocart = () => {
  //   const res = { name: props.name, price: props.price };
  //   setCart((curr) => [...curr, res]);

  // let productsDisplayed = props.data.filter((product) => {
  //   return product.name.includes(props.search);
  // });

  return (
    <div className="container">
      <Banner />
      <div className="products">
        {data.map((dataDetail, i) => {
          return (
            <div className="product" key={i}>
              {dataDetail.id}

              <div className="product-img">
                <img src={dataDetail.image} alt="handbag1" />
              </div>
              <div className="product-details">
                <div className="product-name">{dataDetail.name}</div>
                <div className="product-price">{dataDetail.price}</div>

                <div className="product-qty">{dataDetail.qty}</div>

                <button className="add-to-cart" onClick={props.addtocart(i)}>
                  ADD TO CART
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Products;
